import React from 'react';
import ReactDOM from 'react-dom';
import Board from './components/Board';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

class Game extends React.Component {

    /*
    Beginner: 10 mine, 8*8 board
    Intermediate: 20 mine, 12*12 board
    Expert: 40 mine, 16*16 board
    */

    state = {
        height: 8,
        width: 8,
        mines: 10,
    };

    handleGameStart = () => {
        let difficulty = document.querySelector("#level_select");
        if (difficulty.value === "beginner") {
            this.setState({
                height: 8,
                width: 8,
                mines: 10,
            });
        }
        if (difficulty.value === "intermediate") {
            this.setState({
                height: 12,
                width: 12,
                mines: 20,
            });
        }
        if (difficulty.value === "expert") {
            this.setState({
                height: 16,
                width: 16,
                mines: 40,
            });
        }
    }

    render() {
        const { height, width, mines } = this.state;
        return (
            <div className="game">
                <div className="game-info">
                    <h4>Select your level and Start</h4>
                    <span className="info">Level:
                        <select id="level_select">
                            <option value="beginner"> Beginner </option>
                            <option value="intermediate"> Intermediate </option>
                            <option value="expert"> Advanced </option>
                        </select>
                    </span>
                    <button onClick={this.handleGameStart}>Start</button>
                </div>

                <Board height={height} width={width} mines={mines} />

                <div className="game-info">
                    <h4>Subproject by Kyle Lee</h4>
                </div>

            </div>
        );
    }
}

ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
